color:blue;

margin-top:-320px;
margin-left:300px;


position:absolute;
align-self:'center';


`;

const FullBio = styled.div`
position: fixed;
width:830px;
background:lightgray;
margin-left:340px;
margin-top:200px;
min-height: 310px;




`;


const SchoolList =styled.form`
 background:lightgray;

    margin-right:850px;
    margin-top:10%;
    max-width:230px;
    position:'absolute';
    min-height:370px;
    display: flex;
    padding-top:10px;



    .school-name{
        margin: 0 auto;
        width:50%;
    font-size:13px;

    }

`;



const Button = styled.button`
  background: lightgray;

  height:40px;
  width:170px;
  font-size:15px;
  position:absolute;
  margin-top:-230px;
  margin-left:300px;


`;


  return (
    <React.Fragment>

    <Main>  
    <SchoolList> <span className="school-name">Hi there! Welcome to your education showcase. </span></SchoolList> 
    <Title >Welcome to's {location.state.name} education page  </Title>
    <Button>Add new education</Button>
   <FullBio>
<div className='main-panel'>Type your name and click "Enter" below to begin!</div>

   </FullBio>
   </Main>
   </React.Fragment>

  );
}



export default HomePage;
