import React,{useState, useEffect} from 'react';


import styled from 'styled-components'
import {RouteComponentProps} from 'react-router-dom'

interface Props extends RouteComponentProps {
    options:string[]
}


const AutoComplete: React.FC<Props>=({options:{}})=> {


const [activeOption, setActiveOption] = useState(0);
const [filteredOptions, setFilteredOptions] = useState([]);
const [showOptions, setShowOptions]=useState(false)
const [userInput, setUserInput]=useState("")

 const onChange = (e: { currentTarget: { value: any; }; }) => {
    console.log('onChanges');


    const userInput = e.currentTarget.value;

    const filteredOptions = options.filter(
      (optionName) =>
        optionName.toLowerCase().indexOf(userInput.toLowerCase()) > -1

    );

    let arry= filteredOptions as any

    setActiveOption(0)
    setFilteredOptions(arry)
    setShowOptions(true)
    setUserInput(e.currentTarget.value)

  };
 const onClick = (e: { currentTarget: { innerText: React.SetStateAction<string>; }; }) => {
    setActiveOption(0)
    setFilteredOptions([])
    setShowOptions(false)
    setUserInput(e.currentTarget.innerText)


  };

 const onKeyDown = (e: { keyCode: number; }) => {


    if (e.keyCode === 13) {
        setActiveOption(0)
        setShowOptions(false)
        //setUserInput(filteredOptions[activeOption])

    } else if (e.keyCode === 38) {
      if (activeOption === 0) {
        return;
      }
      setActiveOption(activeOption-1)

    } else if (e.keyCode === 40) {
      if (activeOption === filteredOptions.length - 1) {
        console.log(activeOption);
        return;
      }
      setActiveOption(activeOption+1)

    }
  };

let optionList
if(showOptions && userInput){
    if (filteredOptions.length) {
        optionList = (
          <ul className="options">
            {filteredOptions.map((optionName, index) => {
              let className;
              if (index === activeOption) {
                className = 'option-active';
              }
              return (
                <li className={className} key={optionName} onClick={onClick}>
                  {optionName}
                </li>
              );
            })}
          </ul>
        );
      } else {
        optionList = (
          <div className="no-options">
            <em>No Option!</em>
          </div>
        );
      } 
}


  return (
    <React.Fragment>
    <div className="search">
      <input
        type="text"
        className="search-box"
        onChange={onChange}
        onKeyDown={onKeyDown}
        value={userInput}
      />
      <input type="submit" value="" className="search-btn" />
    </div>
    {optionList}
  </React.Fragment>

  );
}


export default AutoComplete;
