export type Action = {type:'ADD_SCHOOL', payload: string}
import {ADD_SCHOOL,DELETE_SCHOOL, SchoolActionTypes } from './../state/types.js'
export const addSchool = (data:any):Action => ({
  type: ADD_SCHOOL,
  payload: data
})

export const deleteSchool = (data:any) => ({
  type: DELETE_SCHOOL,
  payload: data
})
