    display: flex;
    padding-top:10px;



    .school-name{
        margin: 0 auto;
        width:50%;
    font-size:13px;

    }

`;



const Button = styled.button`
  background: lightgray;

  height:40px;
  width:170px;
  font-size:15px;
  position:absolute;
  margin-top:-230px;
  margin-left:300px;


`;

function addingSchool(){
    setIsOpen(true);

}
function closeModal(){
    setIsOpen(false);
  }
  function afterOpenModal() {
    // references are now sync'd and can be accessed.
