import React,{useState, useEffect} from 'react';

import {RouteComponentProps, withRouter} from 'react-router-dom'
import styled from 'styled-components'
import {Location} from 'history'
import {useSelector, useDispatch} from 'react-redux'
import {SchoolState} from './reducers/schools'
import {addSchool} from './actions'
import Modal from 'react-modal';

type StateType={
    name:string
}
type LocationProps = Location<StateType>;


interface Props  {
    location: LocationProps;

 }
const HomePage:React.FC<Props>=({location})=> {
    const [modalIsOpen,setIsOpen] = React.useState(false);
    const [schooolName, setSchoolName] = useState("");
    const [degree, setDegree] = useState("");
    const [studyField, setStudyField] = useState("");
    const [yearStarted, setYearStarted] = useState("");
    const [yearEnded, setYearEnded] = useState("");
    const [GPA, setGPA] = useState("");
    const [description, setDescription] = useState("");
    // const size = useWindowSize()
//   const panelMargin=  (size.width-360)/2
  //const schools = useSelector<SchoolState, SchoolState['schools']>((state)=>state.schoolReducer.schools)
 // const schools = useSelector((state)=>state)
const schools:any= useSelector((state)=>state)

const dispatch = useDispatch()
  console.log(schools.schoolReducer.schools)
const Main = styled.div`


display: flex;
align-items: center;
justify-content: center;
flex-direction:row;

`
const Title = styled.h4`


margin-top:-320px;
margin-left:300px;


position:absolute;
align-self:'center';


`;

const FullBio = styled.div`
position: fixed;
width:830px;
background:lightgray;
margin-left:340px;
margin-top:200px;
min-height: 310px;




`;


const SchoolList =styled.form`
 background:lightgray;

    margin-right:850px;
    margin-top:10%;
    max-width:230px;
    position:'absolute';
    min-height:370px;
    display: flex;
    padding-top:10px;



    .school-name{
        margin: 0 auto;
        width:50%;
    font-size:13px;

    }

`;



const Button = styled.button`
  background: lightgray;

  height:40px;
  width:170px;
  font-size:15px;
  position:absolute;
  margin-top:-230px;
  margin-left:300px;


`;
const ModalContent = styled.div`
  display: flex;
align-items: center;
justify-content: center;
flex-direction:'column';

`;
const SaveInfoButton = styled.button`
  background: lightgray;

  height:30px;
  width:70px;
  font-size:15px;
  position:absolute;
    bottom:10px;
    right:10px;


`;


function addingSchool(){
    setIsOpen(true);

}
function closeModal(){
    setIsOpen(false);
  }


const customStyles = {
    content : {
      top                   : '40%',
      left                  : '62%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)',
      width                 :  350,

    }
  };
  function modalSaveInfo(){
      console.log(schooolName, degree, studyField, yearStarted, 
        yearEnded, GPA, description)
  }

  return (
    <React.Fragment>

    <Modal
          isOpen={modalIsOpen}

          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Education Modal"

        >
<button onClick={closeModal}>X</button>
<span style={{marginLeft:5}}>Education Information</span> 
        <ModalContent>


          <form onSubmit={modalSaveInfo} style={{paddingTop:10}}>
              <div>
            <label>Name of School</label>
            <br/>
            <input value={schooolName}  onChange={(e)=> e.defaultPrevented } />
            {/* setSchoolName(e.target.value) */}
            </div>
            <div style={{flexDirection:'row'}} >
            <label>Degree</label>
            <br/>
            <input  value={degree}  onChange={(e)=>setDegree(e.target.value)} />
            <div style={{position:'relative', paddingLeft:30,  marginLeft:'50%', marginTop:-39}}>
            <label>Field of Study</label>
            <input  onChange={(e)=>setStudyField(e.target.value)} />

            </div>
            </div>
            <div>

            </div>
            <div style={{flexDirection:'row'}} >
            <label>Year Started</label>
            <br/>
            <input  type="number" onChange={(e)=>setYearStarted(e.target.value)}/>
            <div style={{position:'relative', paddingLeft:30,  marginLeft:'50%', marginTop:-39}}>
            <label>Year Ended</label>
            <input  type="number" onChange={(e)=>setYearEnded(e.target.value)}  />

            </div>
            </div>
            <div>
            <label>GPA</label>
            <br/>
            <input  type='number' step='.01' onChange={(e)=>setGPA(e.target.value)}  />
            </div>
            <div>
            <label>Description</label>
            <br/>
           <textarea  onChange={(e)=>setDescription(e.target.value)} />
            </div>

           <SaveInfoButton type='submit'>
               Save
           </SaveInfoButton>
          </form>
          </ModalContent>

        </Modal>


    <Main>  
    <SchoolList> <span className="school-name">Hi there! Welcome to your education showcase. </span></SchoolList> 
    <Title >Welcome to {location.state.name}'s education page  </Title>
    <Button onClick={addingSchool}>Add new education</Button>
   <FullBio>
<div className='main-panel'>Type your name and click "Enter" below to begin!</div>

   </FullBio>
   </Main>
   </React.Fragment>

  );
}



export default HomePage;
